    COUNT(*) as quantidade,
    STRING_AGG(nome, ', ') as nomes_encontrados,
    STRING_AGG(DISTINCT loja_id::text, ', ') as lojas_associadas,
    STRING_AGG(DISTINCT id::text, ', ') as uuids_vendedores
FROM core.vendedores
WHERE ativo = true
GROUP BY consultor_tipo
ORDER BY quantidade DESC;


