-- 4.1. Consulta específica usando nome_normalizado (mais eficiente)
SELECT 
    id,
    nome,
    nome_normalizado,
    codigo_vendedor,
    loja_id,
    ativo,
    CASE 
        WHEN nome_normalizado LIKE '%beth%' OR nome_normalizado LIKE '%elizabete%' OR nome_normalizado LIKE '%bethania%' THEN 'BETH'
        WHEN nome_normalizado LIKE '%felipe%' THEN 'FELIPE' 
        WHEN nome_normalizado LIKE '%larissa%' THEN 'LARISSA'
        WHEN nome_normalizado LIKE '%taty%' OR nome_normalizado LIKE '%tatiana%' OR nome_normalizado LIKE '%tatiane%' THEN 'TATY'
        WHEN nome_normalizado LIKE '%wevilly%' OR nome_normalizado LIKE '%wevily%' THEN 'WEVILLY'
        WHEN nome_normalizado LIKE '%erika%' THEN 'ERIKA'
        WHEN nome_normalizado LIKE '%rogerio%' THEN 'ROGÉRIO'
        ELSE 'OUTROS'
    END AS consultor_mapeado
FROM core.vendedores
WHERE ativo = true
ORDER BY nome;

