-- 6. Consulta para associar vendedores com lojas (JOIN)
SELECT 
    v.id as vendedor_id,
    v.nome as vendedor_nome,
    v.codigo_vendedor as vendedor_codigo,
    l.id as loja_id,
    l.nome as loja_nome,
    l.codigo as loja_codigo,
    CASE 
        WHEN UPPER(v.nome) LIKE '%BETH%' OR UPPER(v.nome) LIKE '%ELIZABETE%' OR UPPER(v.nome) LIKE '%BETHÂNIA%' THEN 'BETH'
        WHEN UPPER(v.nome) LIKE '%FELIPE%' THEN 'FELIPE' 
        WHEN UPPER(v.nome) LIKE '%LARISSA%' THEN 'LARISSA'
        WHEN UPPER(v.nome) LIKE '%TATY%' OR UPPER(v.nome) LIKE '%TATIANA%' OR UPPER(v.nome) LIKE '%TATIANE%' THEN 'TATY'
        WHEN UPPER(v.nome) LIKE '%WEVILLY%' OR UPPER(v.nome) LIKE '%WEVILY%' THEN 'WEVILLY'
        WHEN UPPER(v.nome) LIKE '%ERIKA%' OR UPPER(v.nome) LIKE '%ÉRIKA%' THEN 'ERIKA'
        WHEN UPPER(v.nome) LIKE '%ROGÉRIO%' OR UPPER(v.nome) LIKE '%ROGERIO%' THEN 'ROGÉRIO'
        ELSE 'OUTROS'
    END AS consultor_mapeado
FROM core.vendedores v
LEFT JOIN core.lojas l ON v.loja_id = l.id
WHERE v.ativo = true AND l.ativo = true
ORDER BY l.nome, v.nome;

